{function add_delete_friend(e){e.click((function(d){d.preventDefault();let n=e.attr("id");$.ajax({url:`/friend?id=${n}`,type:"POST",success:function(d){console.log(d),"Friend added"==d.data.message?e.prop("value","Friend"):e.prop("value","Add Friend")},error:function(e){console.log(e.responseText)}})}))}$(document).ready((function(){const e=$("div.users div");for(let d=0;d<e.length;d++){add_delete_friend($("button",e[d]),e[d])}}))}